package arthimaticoperators;

public class Bitwise {


	public static void main(String[] args) {
		int a=10;
		int b=15;{
			System.out.println(+(a&b));
			System.out.println(+(a|b));
			System.out.println(+(a^b));
			System.out.println(+(a<<2));
			System.out.println(+(b>>2));
			System.out.println(+(~b));
		}
		}
		
		
		// TODO Auto-generated method stub

	}


